{
  "from": {
    "email": "marketing@waymore.io",
    "name": "Waymore Team"
  },
  "subject": "Welcome to Waymore Platform",
  "template": {
    "key": "transactional",
    "locale": "en"
  },
  "to": [
    {
      "email": "{{recipient_email}}",
      "name": "{{recipient_name}}"
    }
  ],
  "variables": {
    "workspace_name": "Acme Corp",
    "user_firstname": "{{user_firstname}}",
    "product_name": "{{product_name}}",
    "support_email": "support@waymore.io",
    "title": {
      "text": "Welcome to Waymore!"
    },
    "body": {
      "content": [
        "Hello {{user_firstname}},",
        "",
        "🎉 <strong>Welcome to {{product_name}}!</strong>",
        "",
        "We're excited to have you on board. Here are some quick tips to get you started:",
        "",
        "• <strong>Explore the dashboard</strong> to see your workspace overview",
        "• <strong>Invite team members</strong> to collaborate on your projects",
        "• <strong>Check out our documentation</strong> for detailed guides",
        "",
        "If you have any questions, don't hesitate to reach out to our support team."
      ]
    },
    "snapshot": {
      "title": "Account Details",
      "style": "table",
      "facts": [
        {"label": "Account Type:", "value": "Pro Plan"},
        {"label": "Workspace:", "value": "Acme Corp"},
        {"label": "Setup Status:", "value": "Complete"}
      ]
    },
    "actions": {
      "primary": {
        "label": "Get Started",
        "url": "https://go.waymore.io/dashboard",
        "style": "button"
      },
      "secondary": {
        "label": "View Documentation",
        "url": "https://docs.waymore.io",
        "style": "link"
      }
    }
  },
  "metadata": {
    "tenantId": "acme_corp",
    "eventId": "welcome_user",
    "notificationType": "welcome"
  }
}

{
  "from": {
    "email": "marketing@waymore.io",
    "name": "Waymore Team"
  },
  "subject": "Payment received for your Pro plan – Acme Corp",
  "template": {
    "key": "transactional",
    "locale": "en"
  },
  "to": [
    {
      "email": "{{recipient_email}}",
      "name": "{{recipient_name}}"
    }
  ],
  "variables": {
    "workspace_name": "Acme Corp",
    "user_firstname": "{{user_firstname}}",
    "product_name": "{{product_name}}",
    "support_email": "support@waymore.io",
    "title": {
      "text": "Payment Received"
    },
    "body": {
      "content": [
        "Hello {{user_firstname}},",
        "",
        "✅ <strong>Payment received successfully!</strong>",
        "",
        "Thank you for your payment. Your Pro plan subscription has been renewed and is now active."
      ]
    },
    "snapshot": {
      "title": "Transaction Details",
      "style": "table",
      "facts": [
        {"label": "Amount:", "value": "$29.00"},
        {"label": "Plan:", "value": "Pro Plan"},
        {"label": "Billing Period:", "value": "Monthly"},
        {"label": "Next Billing Date:", "value": "March 15, 2024"}
      ]
    },
    "actions": {
      "primary": {
        "label": "View Invoice",
        "url": "https://go.waymore.io/billing",
        "style": "button"
      },
      "secondary": {
        "label": "Manage Subscription",
        "url": "https://go.waymore.io/settings/billing",
        "style": "link"
      }
    }
  },
  "metadata": {
    "tenantId": "acme_corp",
    "eventId": "payment_success",
    "notificationType": "billing"
  }
}

{
  "from": {
    "email": "marketing@waymore.io",
    "name": "Waymore Team"
  },
  "subject": "You've used 80% of your Pro plan – Acme Corp",
  "template": {
    "key": "transactional",
    "locale": "en"
  },
  "to": [
    {
      "email": "{{recipient_email}}",
      "name": "{{recipient_name}}"
    }
  ],
  "variables": {
    "workspace_name": "Acme Corp",
    "user_firstname": "{{user_firstname}}",
    "product_name": "{{product_name}}",
    "support_email": "support@waymore.io",
    "title": {
      "text": "Usage Alert"
    },
    "body": {
      "content": [
        "Hello {{user_firstname}},",
        "",
        "⚠️ <strong>You've used 80% of your Pro plan</strong>",
        "",
        "You're approaching your monthly limit. Consider upgrading to avoid any service interruption."
      ]
    },
    "visual": {
      "type": "progress",
      "progress_bars": [
        {
          "label": "API Requests",
          "current": 8000,
          "total": 10000,
          "percentage": 80,
          "unit": "requests",
          "color": "#f59e0b",
          "description": "Monthly usage"
        }
      ]
    },
    "snapshot": {
      "title": "Usage Summary",
      "style": "table",
      "facts": [
        {"label": "Current Usage:", "value": "80%"},
        {"label": "Plan Limit:", "value": "10,000 requests"},
        {"label": "Used:", "value": "8,000 requests"},
        {"label": "Remaining:", "value": "2,000 requests"}
      ]
    },
    "actions": {
      "primary": {
        "label": "Upgrade Plan",
        "url": "https://go.waymore.io/upgrade",
        "style": "button"
      },
      "secondary": {
        "label": "View Usage Details",
        "url": "https://go.waymore.io/usage",
        "style": "link"
      }
    }
  },
  "metadata": {
    "tenantId": "acme_corp",
    "eventId": "usage_warning",
    "notificationType": "usage"
  }
}

{
  "key": "welcome-email",
  "name": "Welcome Email",
  "description": "Welcome new users to the platform with proper fallback syntax",
  "category": "onboarding",
  "variableSchema": {
    "type": "object",
    "properties": {
      "user_name": {"type": "string"},
      "company_name": {"type": "string"},
      "dashboard_url": {"type": "string"}
    },
    "required": ["user_name", "company_name", "dashboard_url"]
  },
  "jsonStructure": {
    "title": {
      "text": "{{title.text|Welcome to Waymore!}}",
      "size": "{{title.size|28px}}",
      "weight": "{{title.weight|700}}",
      "color": "{{title.color|#1f2937}}",
      "align": "{{title.align|center}}"
    },
    "body": {
      "paragraphs": [
        "{{greeting|Hello}} {{user_name|John}}, {{welcome.message|welcome to our platform!}}",
        "{{body.instruction|We're excited to have you on board. Click the button below to get started:}}"
      ],
      "fontSize": "{{body.fontSize|16px}}",
      "lineHeight": "{{body.lineHeight|26px}}"
    },
    "actions": {
      "primary": {
        "label": "{{actions.primary.label|Get Started}}",
        "url": "{{dashboard_url|https://app.example.com}}",
        "style": "{{actions.primary.style|button}}",
        "color": "{{actions.primary.color|#3b82f6}}",
        "text_color": "{{actions.primary.text_color|#ffffff}}"
      }
    },
    "footer": {
      "tagline": "{{footer.tagline|Waymore - Your Success Partner}}",
      "copyright": "{{footer.copyright|© 2024 Waymore. All rights reserved.}}"
    }
  }
}

